import { Usuario } from './../../model/usuario';
import { Observable, Subscription } from 'rxjs';
import { FuncionarioService } from './../../funcionario/funcionario.service';
import { Component, OnInit } from '@angular/core';
import { Funcionario } from 'src/app/model/funcionario';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { UsuarioService } from '../usuario.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-usuario-cadastro',
  templateUrl: './usuario-cadastro.component.html',
  styleUrls: ['./usuario-cadastro.component.css'],
  preserveWhitespaces: true
})
export class UsuarioCadastroComponent implements OnInit {

  funcionarios$!: Observable<Funcionario[]>;
  formulario!: FormGroup;
  novo: boolean = false;
  inscricao: Subscription;
  usuario!: Usuario;

  constructor(
    private formBuilder: FormBuilder,
    private funcionarioService: FuncionarioService,
    private route: ActivatedRoute,
    private router: Router,
    private usuarioService: UsuarioService
  ) {

    this.inscricao = this.route.params.subscribe(
      (params: any) => {
        let id = params['login'];

        if (id == null) {
          this.novo = true;
        } else {

          this.usuarioService.getUsuario(id).subscribe(
            u => {

              this.usuario = u
              if (u) {
                this.povoar();
              }

            }
          );

        }

      }
    );


  }

  ngOnInit(): void {

    this.funcionarios$ = this.funcionarioService.list();


    this.formulario = this.formBuilder.group({
      login: [null, Validators.required],
      senha: [null, [Validators.required, Validators.minLength(3)]],
      funcionario: [null, Validators.required],
      permissao: [null, Validators.required]

    });
  }

  onSubmit() {

    if (this.formulario.valid) {


      if (this.novo) {

        this.usuario = new Usuario();

        this.usuario.funcionario = this.formulario.value['funcionario'];
        this.usuario.senha = this.formulario.value['senha'];
        this.usuario.login = this.formulario.value['login'];
        this.usuario.permissao = this.formulario.value['permissao'];

        this.usuarioService.salvarUsuario(this.usuario).subscribe(

          success => {

            this.formulario.reset();
            this.router.navigate([''])

          },
          erro => {

            console.log(erro)


          }

        );


      } else {

        this.usuario.funcionario = this.formulario.value['funcionario'];
        this.usuario.senha = this.formulario.value['senha'];
        this.usuario.login = this.formulario.value['login'];
        this.usuario.permissao = this.formulario.value['permissao'];

        this.usuarioService.atualizarUsuario(this.usuario).subscribe(

          success => {

            this.formulario.reset();

          },
          erro => {

            console.log(erro)


          }

        );
      }
    } else {
      console.log('não é valido')
    }
  }

  comparar(obj1: Funcionario, obj2: Funcionario){
    return obj1 && obj2 ? (obj1.matricula === obj2.matricula) : obj1 === obj2;
  }

  povoar() {

    this.formulario.get('funcionario')?.setValue(this.usuario.funcionario);
    this.formulario.get('permissao')?.setValue(this.usuario.permissao);
    this.formulario.get('senha')?.setValue(this.usuario.senha);
    this.formulario.get('login')?.setValue(this.usuario.login);

  }

}
<form
  [formGroup]="formulario"
  (ngSubmit)="onSubmit()"
  *ngIf="funcionarios$ | async as funcionario; else loading"
  style="padding: 10px"
>
  <div class="form-group row">
    <label for="funcao" class="col-sm-3 col-form-label">Funcionario:</label>
    <div class="col-sm-9">
      <select
        style="margin: 5px"
        id="funcionario"
        class="form-control"
        formControlName="funcionario"
        [compareWith]="comparar"
      >
        <option *ngFor="let f of funcionario" [ngValue]="f">
          {{ f.matricula }} - {{ f.pessoa.nome }}
        </option>
      </select>
    </div>
  </div>

  <div class="form-group row">
    <label for="funcao" class="col-sm-3 col-form-label">Tipo Acesso:</label>
    <div class="col-sm-9">
      <select
        style="margin: 5px"
        id="permissao"
        class="form-control"
        formControlName="permissao"
      >
        <option value="1">Usuario</option>
        <option value="2">Manutentor</option>
        <option value="3">Administrador</option>
      </select>
    </div>
  </div>

  <div class="form-group row">
    <label for="loginNovo" class="col-sm-3 col-form-label">Login:</label>
    <div class="col-sm-9">
      <input
        type="text"
        class="form-control"
        placeholder="Digite um login"
        formControlName="login"
        id="loginNovo"
      />
    </div>
  </div>

  <div class="form-group row">
    <label for="senhaNova" class="col-sm-3 col-form-label">Senha:</label>
    <div class="col-sm-9">
      <input
        type="password"
        placeholder="Digite uma senha"
        class="form-control"
        formControlName="senha"
        id="senhaNova"
      />
    </div>
  </div>

  <div class="form-group row">
    <div class="col-sm-9">
      <button type="submit" class="btn btn-primary">Salvar</button>
    </div>
  </div>
</form>

<ng-template #loading>
  <div class="spinner-border" role="status">
    <span class="sr-only">Loading...</span>
  </div>
</ng-template>
