import { catchError } from 'rxjs/operators';
import { Observable, empty } from 'rxjs';
import { BsModalRef, BsModalService } from 'ngx-bootstrap/modal';
import { Component, OnInit } from '@angular/core';
import { MaquinaService } from './maquina.service';
import { Maquina } from '../model/maquina';
import { AlertModalComponent } from '../shared/alert-modal/alert-modal.component';

@Component({
  selector: 'app-maquina',
  templateUrl: './maquina.component.html',
  styleUrls: ['./maquina.component.css'],
  preserveWhitespaces: true // usa o espaço no html
})
export class MaquinaComponent implements OnInit {

  bsModalRef!: BsModalRef;
  maquina$!: Observable<Maquina[]>;

  constructor(
    private service: MaquinaService,
    private modalService: BsModalService
  ) { }

  ngOnInit(): void {

    this.maquina$ = this.service.list().pipe(
      catchError(error => {
        this.handleError();
        return empty();
      }

      )
    )
  }


  handleError(){
    this.bsModalRef = this.modalService.show(AlertModalComponent);
    this.bsModalRef.content.type = 'danger';
    this.bsModalRef.content.message = 'Erro ao carregar';
  }

}
<div class="container">

  <nav class="navbar navbar-light bg-light">
    <div class="form-inline">
      <input
        class="form-control mr-sm-2"
        type="search"
        placeholder="Search"
        aria-label="Search"
      />

      <button class="btn btn-outline-success my-2 my-sm-0">Procurar</button>
      <button
        class="btn btn-outline-success my-2 my-sm-0"
        [routerLink]="'cadastrar'"
      >
        Novo
      </button>
    </div>
  </nav>

  <div class="container">
    <table
      class="table table-hover"
      *ngIf="maquina$ | async as maquina; else loading"
    >
      <thead>
        <tr>
          <th>Sigla</th>
          <th>Fabricante</th>
          <th>Setor</th>
          <th>data</th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let m of maquina">
          <td>{{ m.sigla }}</td>
          <td>{{ m.fabricante.pessoa.nome }}</td>
          <td>{{ m.setor.nome }}</td>
          <td>{{ m.dataCompra }}</td>
          <td>
            <span class="float-end">
              <button
                class="btn btn-outline-warning mb-1 btn-sm"
                [routerLink]="'atualizar/' + m.sigla"
              >
                Atualizar
              </button>
            </span>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>

<ng-template #loading>
  <div class="spinner-border" role="status">
    <span class="sr-only">Loading...</span>
  </div>
</ng-template>
