import { MaquinaService } from './../maquina.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Subscription, Observable } from 'rxjs';
import { Component, OnInit } from '@angular/core';
import { Maquina } from 'src/app/model/maquina';
import { Fabricante } from 'src/app/model/fabricante';
import { Setor } from 'src/app/model/setor';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-maquina-cadastro',
  templateUrl: './maquina-cadastro.component.html',
  styleUrls: ['./maquina-cadastro.component.css']
})
export class MaquinaCadastroComponent implements OnInit {

  maquina!: Maquina;

  inscricao: Subscription;
  fabricante$!: Observable<Fabricante[]>;
  setor$!: Observable<Setor[]>;
  formulario!: FormGroup;
  novo: boolean = false;

  constructor(
    private formBuilder: FormBuilder,
    private route: ActivatedRoute,
    private maquinaService: MaquinaService
  ) {
    this.inscricao = this.route.params.subscribe(
      (params: any) => {
        let id = params['id'];

        if (id == null) {
          this.novo = true;
        } else {

          // this.maquinaService.getFuncionario(matricula).subscribe(
          //   f => {

          //     this.funcionario = f
          //     if (f) {
          //       this.povoar();
          //     }

          //   }
          // );

        }

      }
    );
  }

  ngOnInit(): void {


    this.formulario = this.formBuilder.group({
      sigla: [null, [Validators.required, Validators.minLength(3)]],
      dataCompra: [null, Validators.required],
      fabricante: [null, Validators.required],
      setor: [null, Validators.required]

    });

  }

  onSubmit(){

  }

}
<form [formGroup]="formulario" (ngSubmit)="onSubmit()">

  <div class="form-group row">
    <label for="sigla" class="col-sm-3 col-form-label">Sigla:</label>
    <div class="col-sm-9">
      <input
        type="text"
        class="form-control"
        autocomplete="off"
        placeholder="Digite uma Sigla"
        formControlName="sigla"
        id="siglaNova"
      />
    </div>
  </div>

  <div class="form-group row">
    <label for="dataCompra" class="col-sm-3 col-form-label">Data Compra:</label>
    <div class="col-sm-9">
      <input
        type="date"
        class="form-control"
        autocomplete="off"
        formControlName="dataCompra"
        id="dataCompra"
      />
    </div>
  </div>

  <div
    class="form-group row"
    *ngIf="fabricante$ | async as fabricante; else loading"
    style="padding: 10px"
  >
    <label for="funcao" class="col-sm-3 col-form-label">Fabricante:</label>
    <div class="col-sm-9">
      <select
        style="margin: 5px"
        id="funcionario"
        class="form-control"
        formControlName="funcionario"
      >
        <option *ngFor="let f of fabricante" [ngValue]="f">
          {{ f.id }} - {{ f.nome }}
        </option>
      </select>
    </div>
  </div>

  <div class="form-group row">
    <label for="funcao" class="col-sm-3 col-form-label">Tipo Acesso:</label>
    <div class="col-sm-9">
      <select
        style="margin: 5px"
        id="permissao"
        class="form-control"
        formControlName="permissao"
      >
        <option value="1">Usuario</option>
        <option value="2">Manutentor</option>
        <option value="3">Administrador</option>
      </select>
    </div>
  </div>


  <div class="form-group row">
    <div class="col-sm-9">
      <button type="submit" class="btn btn-primary">Salvar</button>
    </div>
  </div>
</form>

<ng-template #loading>
  <div class="spinner-border" role="status">
    <span class="sr-only">Loading...</span>
  </div>
</ng-template>
