import { Observable } from 'rxjs';
import { FuncionarioService } from './../../funcionario/funcionario.service';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Maquina } from 'src/app/model/maquina';
import { MaquinaService } from 'src/app/maquina/maquina.service';

@Component({
  selector: 'app-os-cadastrar',
  templateUrl: './os-cadastrar.component.html',
  styleUrls: ['./os-cadastrar.component.css']
})
export class OsCadastrarComponent implements OnInit {


  formulario!: FormGroup;
  maquina$!: Observable<Maquina[]>
  oficina: string[] = ['Calderaria','Eletrica', 'Mecanica']


  constructor(
    private formBuilder: FormBuilder,
    private route: ActivatedRoute,
    private router: Router,
    private funcionarioService: FuncionarioService,
    private maquinaService: MaquinaService
  ) { }

  ngOnInit() {

    this.maquina$ = this.maquinaService.list().pipe();

    this.formulario = this.formBuilder.group({

      problema: [null, [Validators.required, Validators.minLength(3)]],
      maquina:  [null, Validators.required],
      oficina:  [null, Validators.required]

    });

  }

  onSubmit(){

  }

}
<div class="container" style="padding-top: 10px; width: 60%">
  <form
    [formGroup]="formulario"
    (ngSubmit)="onSubmit()"
    *ngIf="maquina$ | async as maquina; else loading"
  >
    <legend>Gerar nova OS</legend>

    <div class="row md-6">
        <label for="maquina" class="col-sm-3 col-form-label">Maquina:</label>
        <div class="col-sm-9">
          <select
            style="margin: 5px"
            id="maquina"
            class="form-control"
            formControlName="maquina"

          >
            <option *ngFor="let m of maquina" [ngValue]="m">{{ m.sigla }}</option>
          </select>
        </div>
      </div>

    <div class="row md-6">
      <label for="problema" class="col-sm-3 col-form-label">Problema:</label>
      <div class="col-sm-9">
        <textarea
          style="margin: 5px"
          type="text"
          placeholder="Descreva o problema da maquina"
          class="form-control"
          formControlName="problema"
        ></textarea>
      </div>
    </div>

    <div class="row md-6">
        <label for="oficia" class="col-sm-3 col-form-label">Oficina:</label>
        <div class="col-sm-9">
          <select
            style="margin: 5px"
            id="oficina"
            class="form-control"
            formControlName="oficina"

          >
            <option *ngFor="let o of oficina" >{{ o }}</option>
          </select>
        </div>
      </div>



    <button type="submit" class="btn btn-primary">Salvar</button>
  </form>
</div>

<ng-template #loading>
  <div class="spinner-border" role="status">
    <span class="sr-only">Loading...</span>
  </div>
</ng-template>
