import { SetorService } from './../../setor/setor.service';
import { MaquinaService } from './../maquina.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Subscription, Observable, empty } from 'rxjs';
import { Component, OnInit } from '@angular/core';
import { Maquina } from 'src/app/model/maquina';
import { Fabricante } from 'src/app/model/fabricante';
import { Setor } from 'src/app/model/setor';
import { ActivatedRoute, Router } from '@angular/router';
import { BsModalRef, BsModalService } from 'ngx-bootstrap/modal';
import { SetorComponent } from 'src/app/setor/setor.component';
import { catchError } from 'rxjs/operators';
import { FabricanteComponent } from 'src/app/fabricante/fabricante.component';
import { FabricanteService } from 'src/app/fabricante/fabricante.service';


@Component({
  selector: 'app-maquina-cadastro',
  templateUrl: './maquina-cadastro.component.html',
  styleUrls: ['./maquina-cadastro.component.css']
})
export class MaquinaCadastroComponent implements OnInit {

  maquina!: Maquina;
  submitted = false;
  bsModalRef!: BsModalRef

  inscricao: Subscription;
  fabricante$!: Observable<Fabricante[]>;
  setor$!: Observable<Setor[]>;
  formulario!: FormGroup;
  novo: boolean = false;

  constructor(
    private formBuilder: FormBuilder,
    private route: ActivatedRoute,
    private router: Router,
    private maquinaService: MaquinaService,
    private setorService: SetorService,
    private fabricanteService: FabricanteService,
    private modalService: BsModalService
  ) {
    this.inscricao = this.route.params.subscribe(
      (params: any) => {
        let id = params['id'];

        if (id == null) {
          this.novo = true;
          this.maquina = new Maquina();
        } else {

          // this.maquinaService.getFuncionario(matricula).subscribe(
          //   f => {

          //     this.funcionario = f
          //     if (f) {
          //       this.povoar();
          //     }

          //   }
          // );

        }

      }
    );
  }

  ngOnInit(): void {


    this.formulario = this.formBuilder.group({
      sigla: [null, [Validators.required, Validators.minLength(3), Validators.maxLength(3)]],
      dataCompra: [null, Validators.required],
      fabricante: [null, Validators.required],
      setor: [null, Validators.required]

    });

    this.onRefresh();



  }

  onRefresh() {
    this.setor$ = this.setorService.list().pipe();
    this.fabricante$ = this.fabricanteService.list().pipe();

  }

  onSubmit() {

    this.submitted = true;



    if (this.formulario.valid) {
      this.maquina.sigla = this.formulario.value['sigla'];
      this.maquina.dataCompra = this.formulario.value['dataCompra'];
      this.maquina.setor = this.formulario.value['setor'];
      this.maquina.fabricante = this.formulario.value['fabricante'];

      this.maquinaService.salvarMaquina(this.maquina).subscribe(
        success => {
          this.formulario.reset();
          this.router.navigate(["/maquina"])

        },
        erro => {

          console.log(erro);
        }
      );

    }
  }

  addSetor() {

    this.bsModalRef = this.modalService.show(SetorComponent);

  }


  addFabricante() {

    this.bsModalRef = this.modalService.show(FabricanteComponent);

  }

  hasError(field: string) {


    return this.formulario.get(field)?.errors

  }

}
<form [formGroup]="formulario" (ngSubmit)="onSubmit()">
  <div class="form-group row">
    <label for="sigla" class="col-sm-3 col-form-label">Sigla:</label>
    <div class="col-sm-9">
      <input
        type="text"
        class="form-control"
        autocomplete="off"
        placeholder="Digite uma Sigla"
        formControlName="sigla"
        id="siglaNova"
        [ngClass]="{ 'is-invalid': submitted && hasError('sigla') }"
      />
      <div class="invalid-feedback">Digite a sigla</div>
    </div>
  </div>

  <div class="form-group row">
    <label for="dataCompra" class="col-sm-3 col-form-label">Data Compra:</label>
    <div class="col-sm-9">
      <input
        type="date"
        class="form-control"
        autocomplete="off"
        formControlName="dataCompra"
        id="dataCompra"
        [ngClass]="{ 'is-invalid': submitted && hasError('dataCompra') }"
      />
      <div class="invalid-feedback">Digite a Data da compra</div>
    </div>
  </div>

  <div class="form-group row">
    <label for="fabricante" class="col-sm-3 col-form-label">Fabricante:</label>
    <div class="col-sm-8">
      <select
        style="margin: 5px"
        id="fabricante"
        class="form-control"
        formControlName="fabricante"
        [ngClass]="{ 'is-invalid': submitted && hasError('fabricante') }"
      >
        <option *ngFor="let f of fabricante$ | async" [ngValue]="f">
          {{ f.id }} - {{ f.pessoa.nome }}
        </option>
      </select>
      <div class="invalid-feedback">Selecione um Fabricante</div>
    </div>
    <div class="col-sm-1">
      <i
        (click)="addFabricante()"
        class="bi bi-plus-circle-fill"
        style="font-size: 2rem; color: rgb(30, 126, 11)"
        tooltip="adicionar novo Fabricante"
      ></i>
    </div>
  </div>

  <div class="form-group row">
    <label for="setor" class="col-sm-3 col-form-label">Setor:</label>
    <div class="col-sm-8">
      <select
        style="margin: 5px"
        id="setor"
        class="form-control"
        formControlName="setor"
        [ngClass]="{ 'is-invalid': submitted && hasError('setor') }"
      >
        <option *ngFor="let s of setor$ | async" [ngValue]="s">
          {{ s.id }} - {{ s.nome }}
        </option>
      </select>
      <div class="invalid-feedback">Selecione um Setor</div>
    </div>
    <div class="col-sm-1">
      <i
        (click)="addSetor()"
        class="bi bi-plus-circle-fill"
        style="font-size: 2rem; color: rgb(30, 126, 11)"
        tooltip="adicionar novo setor"
      ></i>
    </div>
  </div>

  <div class="form-group row">
    <div class="col-sm-9">
      <button type="submit" class="btn btn-primary">Salvar</button>
    </div>
  </div>
  <div class="form-group row">
    <div class="col-sm-9">
      <button type="button" class="btn btn-primary">Atualizar</button>
    </div>
  </div>
</form>

<ng-template #novoFabricante>
  <div class="spinner-border" role="status">
    <span class="sr-only">Loading...</span>
  </div>
</ng-template>
