import { Component, OnInit } from "@angular/core";
import { BsModalRef, BsModalService } from "ngx-bootstrap/modal";
import { empty, Observable } from "rxjs";
import { catchError } from "rxjs/operators";
import { Os } from "src/app/model/os";
import { AlertModalComponent } from "src/app/shared/alert-modal/alert-modal.component";
import { OsService } from "../os.service";

@Component({
  selector: "app-os-atualizar",
  templateUrl: "./os-atualizar.component.html",
  styleUrls: ["./os-atualizar.component.css"],
})
export class OsAtualizarComponent implements OnInit {
  os$!: Observable<Os[]>;

  bsModalRef!: BsModalRef;

  constructor(
    private osService: OsService,
    private modalService: BsModalService
  ) {}

  ngOnInit(): void {
    this.os$ = this.osService.list().pipe(
      catchError((error) => {
        this.handleError();
        return empty();
      })
    );
  }

  handleError() {
    this.bsModalRef = this.modalService.show(AlertModalComponent);
    this.bsModalRef.content.type = "danger";
    this.bsModalRef.content.message = "Erro ao carregar";
  }
}
<div class="container" *ngIf="os$ | async as os; else loading">
    <form>
        <div class="form-row">
            <div class="form-group col-md-6">
                <label for="osNumero">OS</label>
                <input type="text" readonly class="form-control-plaintext" id="osNumero">
            </div>
            <div class="form-group col-md-6">
                <label for="maquina">Maquina</label>
                <input type="text" readonly class="form-control-plaintext" id="maquina">
            </div>
        </div>
    </form>
</div>
